,p_sequence=>70
,p_script_type=>'INSTALL'
,p_script_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'create or replace package body plg_odata_filters as',
'',
'-------------------------------------------------------------------------------',
'-- Name: plg_odata_filters.pks',
