'--=============================================================================',
'-- Constants',
'--=============================================================================',
'c_scope                 constant varchar2(129) := $$plsql_unit || ''.'';',
'c_quote                 constant varchar2(1)   := '''''''';',
'--',
'c_odata_date_format     constant varchar2(10) := ''YYYY-MM-DD'';',
'c_odata_datetime_format constant varchar2(26) := ''YYYY-MM-DD"T"HH24:MI:SS"Z"'';',
'',
