'                and p_plugin_attributes.filter_startswith',
'',
'        then  -- Variant 2: Oracle Style ''ABC%''; mapping to ODATA "startswith"',
'',
'            apex_debug.info( p_message => ''(not) like - Variant 2: Oracle Style ABC*'' );',
'',
'            l_odata_filter :=',
'                get_case_sensitive_filter(',
