,p_script_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'create or replace package body plg_odata_connector as',
'',
'-------------------------------------------------------------------------------',
'-- Name: plg_odata_filters.pks',
'-- Copyright (c) 2012, 2022 Oracle and/or its affiliates.',
