 p_id=>wwv_flow_api.id(390434508065898121)
,p_plug_name=>'Restoring Universal Theme Subscription'
,p_region_template_options=>'#DEFAULT#:t-ContentBlock--h2'
,p_plug_template=>wwv_flow_api.id(873087052820635270)
