'',
'    sys.htp.p(''',
'<div class="js-regionDialog js-resizable js-modal js-draggable js-dialog-size480x320" title="''||apex_escape.html_attribute(l_mdl_title)||''" id="status-completeness-modal-'' || c_region_static_id || ''" style="display:none;">',
