'  c.customer_name account_name,',
'  slsc.status_code lead_status,',
'  ls.lead_source,',
'  l.updated,',
'  t.territory_name,',
'  case',
'    when length(l.lead_details) <= 50',
'    then l.lead_details',
'    else substr(l.lead_details, 1, 50) || ''...''',
'  end lead_details,',
'  l.lead_name_01 name',
'from eba_sales_leads l',
'left join eba_sales_customers c',
