'    return false;',
'  }',
'}',
'  ',
'function resetFilters() {',
'  $search.val(null);',
'  $include.find(''input'').prop(''checked'', true);',
'  ',
'  $searchResultsReg.trigger(''apexrefresh'');',
'}',
'',
