'        and (',
'          instr(upper(o.deal_name), upper(:P116_SEARCH)) > 0',
'            or instr(upper(o.row_key), upper(:P116_SEARCH)) > 0',
'            or instr(upper(o.deal_summary), upper(:P116_SEARCH)) > 0',
'            or instr(upper(o.tags), upper(:P116_SEARCH)) > 0',
'        )',
