'  select ''Account'' source,',
'    4 sort_order,',
'    c.customer_name search_title,',
'    nvl(c.customer_description, ''*** no description provided ***'') search_desc,',
'    c.updated,',
'    apex_util.prepare_url(''f?p='' || :APP_ID || '':94:'' || :APP_SESSION || '':::RP,94:P94_ID:'' || c.id) search_link,',
