'      instr(:P116_INCLUDE,''ACCOUNTS'') > 0 ',
'        and (',
'          instr(upper(c.customer_name), upper(:P116_SEARCH)) > 0',
'            or instr(upper(c.row_key), upper(:P116_SEARCH)) > 0',
