'    c.updated,',
'    apex_util.prepare_url(''f?p='' || :APP_ID || '':94:'' || :APP_SESSION || '':::RP,94:P94_ID:'' || c.id) search_link,',
'    '''' secondary_link,',
'    c.row_key',
'  from eba_sales_customer_contacts c',
