begin
wwv_flow_imp_page.create_page(
 p_id=>1
,p_name=>'Locks'
,p_alias=>'HOME'
,p_step_title=>'&APP_NAME. - Locks'
,p_warn_on_unsaved_changes=>'N'
,p_autocomplete_on_off=>'OFF'
,p_javascript_code=>wwv_flow_string.join(wwv_flow_t_varchar2(
'function showErrors(errors) {',
'  apex.message.clearErrors();',
'  apex.message.showErrors(errors);',
'};',
'',
'function fetchFilteredLocksCount() {',
'  return new Promise((resolve, reject) => {',
'    apex.server.process(''get_filtered_locks'', {}, {',
'      success: (res) => {',
'        if (res.result) {',
'          resolve(res.count);',
'        } else {',
'          reject(res.msg);',
'        }',
'      }',
'    });',
'  });',
'}',
'',
'function unlockLock(confirmMsg, lockId, userId) {',
'  apex.message.confirm(confirmMsg, function(okPressed) {',
'    if (okPressed) {',
'      apex.server.process(''unlock_lock'', { x01: lockId, x02: userId }, {',
'        success: (res) => {',
'          if (res.result) {',
'            apex.region(''loki__locks-list'').refresh();',
'            apex.region(''loki__locks-search'').refresh();',
'          } else {',
'            showErrors([{ ',
'              type: "error",',
'              location: "page",',
'              message: res.msg',
'            }]);',
'          }',
'        }',
'      });',
'    }',
'  });',
'}',
'',
'async function unlockLocks() {',
'  let locksCount = 0;',
'',
'  try {',
'    locksCount = await fetchFilteredLocksCount();',
'  } catch (error) {',
'    showErrors([{',
'      type: "error",',
'      location: [ "page" ],',
'      message: error.message,',
'      unsafe: false',
'    }]);',
'    return;',
'  }',
'',
'  if (!locksCount) {',
'    showErrors([{',
'      type: "error",',
'      location: [ "page" ],',
'      message: "No active locks to unlock",',
'      unsafe: false',
'    }]);',
'    return;',
'  }',
'',
'  const confirmMsg = `Do you want to unlock ${locksCount} locks?`;',
'',
'  apex.message.confirm(confirmMsg, function(okPressed) {',
