wwv_flow_imp_page.create_page_process(
 p_id=>wwv_flow_imp.id(15628218640587121)
,p_process_sequence=>50
,p_process_point=>'ON_DEMAND'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'get_filtered_locks'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'  l_context   apex_exec.t_context;',
'  l_page_id   apex_application_page_regions.page_id%type;',
'  l_region_id apex_application_page_regions.region_id%type;',
'  l_response  json_object_t := json_object_t();',
'begin',
'  l_response.put(''result'', true);',
'',
'  begin',
'    select page_id, region_id',
'      into l_page_id, l_region_id',
'      from apex_application_page_regions',
'     where application_name = ''Loki''',
'       and page_name = ''Locks''',
'       and static_id = ''loki__locks-list'';',
'    ',
'    l_context := apex_region.open_query_context(',
'      p_page_id     => l_page_id,',
'      p_region_id   => l_region_id',
'    );',
'',
'    apex_collection.create_or_truncate_collection(',
'      p_collection_name => ''LOKI_FILTERED_LOCKS''',
'    );',
'',
'    while apex_exec.next_row(l_context) loop',
'      if apex_exec.get_varchar2(',
'          p_context     => l_context, ',
'          p_column_name => ''STATUS''',
'        ) = ''Active'' then',
'',
'        apex_collection.add_member(',
'          p_collection_name => ''LOKI_FILTERED_LOCKS'',',
'          p_c001            => apex_exec.get_varchar2(',
'            p_context     => l_context,',
'            p_column_name => ''LOCK_ID''',
'          ),',
'          p_c002            => apex_exec.get_varchar2(',
'            p_context     => l_context,',
'            p_column_name => ''USER_ID''',
'          )',
'        );',
'      end if;',
'    end loop;',
'',
'    l_response.put(''count'', apex_collection.collection_member_count(',
'      p_collection_name => ''LOKI_FILTERED_LOCKS''',
'    ));',
'    ',
'  exception ',
'    when no_data_found then',
'      l_response.put(''count'', 0);',
'    when others then',
'      l_response.put(''result'', false);',
'      l_response.put(''msg'', substr(sqlerrm(), 1, 2000));',
'  end;',
'',
'  htp.p(l_response.to_clob());',
'end;'))
,p_process_clob_language=>'PLSQL'
,p_internal_uid=>15628218640587121
);
