prompt --application/deployment/install/install_version_24_1_0_set_version
begin
wwv_flow_imp_shared.create_install_script(
 p_id=>wwv_flow_imp.id(34464754315805829)
,p_install_id=>wwv_flow_imp.id(32465273894098598)
,p_name=>'Version 24.1.0: Set Version'
,p_sequence=>160
,p_script_type=>'INSTALL'
,p_script_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'create or replace function check_version (',
'  i_version in varchar2',
') return boolean is',
'  c_curr_version        constant varchar2(6) := ''24.1.0'';',
'  l_cur_version_numbers apex_t_number;',
'  l_inc_version_numbers apex_t_number;',
'begin',
'  loki_util.assert(',
'                  i_condition => regexp_like(',
'                    i_version,',
'                    ''^\d+\.\d+\.\d+$''',
'                  ),',
'                  i_message => ''LOKI: Invalid version format''',
'  );',
'',
'  l_cur_version_numbers := apex_string.split_numbers(',
'                                                    c_curr_version,',
'                                                    ''.''',
'                           );',
'  l_inc_version_numbers := apex_string.split_numbers(',
'                                                    i_version,',
'                                                    ''.''',
'                           );',
'  if l_cur_version_numbers(1) < l_inc_version_numbers(1) then',
'    return true;',
'  end if;',
'  if',
'    l_cur_version_numbers(1) = l_inc_version_numbers(1)',
'    and l_cur_version_numbers(2) < l_inc_version_numbers(2)',
'  then',
'    return true;',
'  end if;',
'',
'  if',
'    l_cur_version_numbers(1) = l_inc_version_numbers(1)',
'    and l_cur_version_numbers(2) = l_inc_version_numbers(2)',
'    and l_cur_version_numbers(3) < l_inc_version_numbers(3)',
'  then',
'    return true;',
'  end if;',
'',
'  return false;',
'end check_version;',
'/'))
);
end;
